import './ExpenseItem.css';
import ExpenseDate from './ExpenseDate';
import Card from '../UI/Card';
import ExpenseItemTitle from './ExpenseItemTitle';
import React, { useState } from 'react';

const ExpenseItem = (props) => {
    const [filteredExpenses, setFilteredExpenses] = useState(2022);
    // props.expenses.filter((expense) => {

    //     console.log(filteredExpenses);
    //     let date = expense.date.getFullYear();
    //     console.log(date)
    //     return date == props.filter;
    // })

    console.log(filteredExpenses);
    return (
        <Card className="expenses">
            {props.expenses.map((expense) => (
                <div className="expense-item" key={expense.id}>
                    <ExpenseDate date={expense.date} />
                    <ExpenseItemTitle title={expense.title} />
                    <div className="expense-item__price">${expense.amount}</div>
                </div>
            ))}
        </Card>
    );
};

export default ExpenseItem;
