import './NewExpense.css';
import React, { useState } from 'react';
import ExpenseForm from './ExpenseForm';

const NewExpense = (props) => {
    const [condiction, setCondiction] = useState(
        <button onClick={changeCondiction} className="new-expense__button">
            ADD A EXPENSE
        </button>
    );

    const changeCondiction = () => {
        setCondiction(<ExpenseForm onSaveExpenseData={props.onSaveExpenseData} />);
    };

    return <div className="new-expense">{condiction}</div>;
};

export default NewExpense;
