import ExpenseItem from './components/Expenses/ExpenseItem';
import NewExpense from './components/NewExpense/NewExpense';
import ExpensesFilter from './components/Expenses/ExpensesFilter';
import React, { useState } from 'react';
import './App.css';

const INICIAL_EXPENSES = [
    {
        id: 'e1',
        title: 'Toilet Paper',
        amount: 94.12,
        date: new Date(2019, 7, 14),
    },
    { id: 'e2', title: 'New TV', amount: 799.49, date: new Date(2021, 2, 12) },
    {
        id: 'e3',
        title: 'Car Insurance',
        amount: 294.67,
        date: new Date(2019, 2, 28),
    },
    {
        id: 'e4',
        title: 'New Desk (Wooden)',
        amount: 450,
        date: new Date(2022, 5, 12),
    },
];

const App = () => {
    const [filterValue, setFilterValue] = useState(2022);
    const [expenses, setExpenses] = useState(INICIAL_EXPENSES);

    const onSaveExpenseData = (expense) => {
        setExpenses((previousStat) => {
            return [expense, ...previousStat];
        });
    };

    const enteringFilter = (value) => {
        setFilterValue(value);
    };

    const removeExpense = (event) => {
        const id = event;
        // setFilteredExpenses(
        //     expenses.filter((expense) => {
        //         return expense.id !== event.target.parentNode.id && expense.date.getFullYear() == filter;
        //     })
        // );
        // console.log(filteredExpenses);
        const index = expenses
            .map((expense) => {
                return expense.id;
            })
            .indexOf(id);
        expenses.slice(index, 1);
    };

    return (
        <div>
            <NewExpense onSaveExpenseData={onSaveExpenseData} />
            <div className="expenses">
                <ExpensesFilter enteringFilter={enteringFilter} filter={enteringFilter} />
                <ExpenseItem expenses={expenses} filter={filterValue} remove={removeExpense} />
            </div>
        </div>
    );
};

export default App;
